            "Chrome" to false,
            "Spotify" to true,
            "Netflix" to false,
            "YouTube" to true,
            "Maps" to false
        )

        LazyColumn(
            modifier = Modifier.weight(1f)
        ) {
            items(sampleApps) { (appName, isSelected) ->
                var checked by remember { mutableStateOf(isSelected) }

